				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\int \limits _0^\infty \rho _{xxx}^r\psi dx$$\end{document}, ∫0∞ρ~xxxr(ρr-ρ∗)ψdx\documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym}
				\usepackage{amsfonts}
				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\int \limits _0^\infty \tilde{\rho }_{xxx}^r(\rho ^r-\rho _*)\psi dx$$\end{document}, ∫0∞ρ~xxxr(ρr-ρ∗)φxdx\documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym}
				\usepackage{amsfonts}
